// Import dependencies
const express = require('express');
const bodyParser = require('body-parser');
const app = express();
const port = 3000;

// Middleware to parse incoming form data (application/x-www-form-urlencoded)
app.use(bodyParser.urlencoded({ extended: true }));
// Middleware to parse incoming JSON data (application/json)
app.use(bodyParser.json());

// Serve the static HTML file (the form)
app.get('/', (req, res) => {
    res.sendFile(__dirname + '/index.html');
});

// Handle POST request for form submission
app.post('/submit-form', (req, res) => {
    const { name, email, subject, message } = req.body;

    // Log the received data to the console (you can process or store it here)
    console.log("User Details Submitted:");
    console.log("Name:", name);
    console.log("Email:", email);
    console.log("Subject:", subject);
    console.log("Message:", message);

    // Send a response back to the client (the front-end)
    res.send(`Thank you, ${name}! Your message has been received.`);
});

// Start the server
app.listen(port, () => {
    console.log(`Server is running on http://localhost:${port}`);
});
